import React, { useState } from 'react';
import './NotFound.css';

function NotFound() {
  const [torchPosition, setTorchPosition] = useState({ top: 0, left: 0 });

  const handleMouseMove = (event) => {
    setTorchPosition({
      top: event.pageY,
      left: event.pageX,
    });
  };

  return (
    <div className="not-found" onMouseMove={handleMouseMove}>
      <div className="text">
        <h1>404</h1>
        <h2>Uh, Ohh</h2>
        <h3>Sorry we can't find what you are looking for 'cuz it's so dark in here</h3>
      </div>
      <div 
        className="torch"
        style={{
          top: torchPosition.top - 100, // Centering the torch
          left: torchPosition.left - 100
        }}
      />
    </div>
  );
}

export default NotFound;
