import React, { useState } from 'react';
import '../Animation/Circles.css';

const CIrcles = () => {
  const [blobPosition, setBlobPosition] = useState({ top: "50%", left: "50%", angle: 0 });

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Calculate the angle between the center of the card and the mouse position
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const deltaX = x - centerX;
    const deltaY = y - centerY;
    const angle = Math.atan2(deltaY, deltaX) * (180 / Math.PI);

    setBlobPosition({
      top: `${y}px`,
      left: `${x}px`,
      angle: angle,
    });
  };

  return (
    <div className="card" onMouseMove={handleMouseMove}>
      <div
        className="blob"
        style={{
          top: blobPosition.top,
          left: blobPosition.left,
          transform: `translate(-50%, -50%) rotate(${blobPosition.angle}deg)`,
        }}
      ></div>
    </div>
  );
};

export default CIrcles;
