import React, { useState } from 'react'

import '../Contact-Main/ContactMain.css'
import { useNavigate } from 'react-router-dom';
import right from '../Images/right-arrow-svgrepo-com.svg'

function ContactMain() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState({});

  const handleEmailChange = (e) => setEmail(e.target.value);

  const validate = () => {
    const error = {};

    if (!email) error.email = 'Email is required';
    else if (!email.includes('@')) error.email = 'Invalid email';

    setError(error);
    return error;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length === 0) {
      // Process the form
      console.log({ name, email, message });
    }
  };

  const navigate = useNavigate();



  const handleClick = (targetPath) => {
      const button = document.querySelector('.slides-button button.active');
      if (button) {
          button.classList.remove('active');
      }
      const clickedButton = document.activeElement;
      clickedButton.classList.add('active');

      setTimeout(() => {
          navigate(targetPath);
      }, 2500);
  };
  return (
    <div>
      <div className='Container'>
        <h1>CONTACT ME</h1>
        <div className='box'>


          <div className='input'>


            <div className='box-2'>

              <div className="input-field">
                <input
                  type="text"
                  required
                  spellCheck="false"
                  id="name-input"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <label htmlFor="name-input">Enter your name</label>
              </div>


              <div className="input-field">
                <input
                  type="text"
                  required
                  spellCheck="false"
                  id="name-input"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
                <label htmlFor="name-input">Enter your Email</label>
              </div>

            </div>








            <div className="input-field-3">
              <input
                type="text"
                required
                spellCheck="false"
                id="name-input"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
              />
              <label htmlFor="name-input">Enter your Message</label>
            </div>

          </div>
        </div>


        <div className='slides-button'>
                    <button onClick={() => handleClick('/projects')}>
                        <img width="30" height="30" src={right} alt="right--v1" />
                        <span>Send message</span>
                    </button>

                </div>



      </div>
    </div>
  )
}

export default ContactMain;
