import React, { useState } from 'react';
import Circles from '../Animation/Circles';

function Home() {
  const [cursorPosition, setCursorPosition] = useState({ top: "50%", left: "50%" });

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setCursorPosition({
      top: `${y}px`,
      left: `${x}px`,
    });
  };

  return (
    <div className='body'>
      <div className='container' onMouseMove={handleMouseMove}>
        <Circles cursorPosition={cursorPosition} />
        <h1 className='animate__fadeInDown'>MUHAMMADABDULLO</h1>
        <h3 className='animate__fadeInDown'>WEB-DEVELOPER</h3>
      </div>
    </div>
  );
}

export default Home;
