import React, { useEffect, useState, useRef } from 'react';
import './Circles.css'; 

const colors = [
  "#ffb56b", "#fdaf69", "#f89d63", "#f59761", "#ef865e", "#ec805d", "#e36e5c",
  "#df685c", "#d5585c", "#d1525c", "#c5415d", "#c03b5d", "#b22c5e", "#ac265e",
  "#9c155f", "#950f5f", "#830060", "#7c0060", "#680060", "#60005f", "#48005f",
  "#3d005e"
];

const Circles = () => {
  const [coords, setCoords] = useState({ x: 0, y: 0 });
  const circlesRef = useRef([]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setCoords({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  useEffect(() => {
    const circles = circlesRef.current;
    let animationFrameId;

    const animateCircles = () => {
      let x = coords.x;
      let y = coords.y;

      circles.forEach((circle, index) => {
        if (circle) {
          const offsetX = (index - (circles.length / 2)) * 20; 
          const offsetY = (index - (circles.length / 2)) * 20; 

    circle.style.left = x - 12 + "px";
          circle.style.top = y - 12 + "px";

          circle.style.transform = `scale(${(circles.length - index) / circles.length})`;
        }
      });

      animationFrameId = requestAnimationFrame(animateCircles);
    };

    animateCircles();

    return () => cancelAnimationFrame(animationFrameId);
  }, [coords]);

  return (
    <div className="circles-container">
      {Array.from({ length: 20 }).map((_, index) => (
        <div
          key={index}
          className="circle"
          ref={(el) => circlesRef.current[index] = el}
          style={{ backgroundColor: colors[index % colors.length] }}
        />
      ))}
    </div>
  );
};

export default Circles;
