import React, { useState, useRef, useEffect } from 'react';

function Navbar() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const mobileMenuRef = useRef(null);

    // Handle hamburger menu click
    const handleMenuOpen = () => {
        setIsMenuOpen(true);
    };

    const handleMenuClose = () => {
        setIsMenuOpen(false);
    };

    // Handle screen resize, close the menu on larger screens (if desired)
    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth > 770) {
                setIsMenuOpen(false); // Optional: close the menu on larger screens automatically
            }
        };
        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    return (
        <div>
            <div className="navbar">
                <div className="nav-logo">
                    <a href="#">Logo</a>
                </div>

                <div id="hamburger-menu" onClick={handleMenuOpen}>&#9776;</div>
            </div>

            <div
                id="mobile-menu"
                ref={mobileMenuRef}
                style={{
                    display: 'flex',
                    transform: isMenuOpen ? 'translateX(0%)' : 'translateX(-100%)',
                    transition: 'transform 0.3s ease',
                    position: 'fixed',
                    top: '0',
                    left: '0',
                    height: '100%',
                    width: '100%',
                    backgroundColor: '#333',
                    zIndex: '9999',
                }}
             >
                <div className="mobile-nav-items">
                    <ul>
                        <li><a href="#">Home</a></li>
                        <li><a href="#">Ptojects</a></li>
                        <li><a href="#">About</a></li>
                        <li><a href="#">Contact</a></li>
                    </ul>
                </div>

                <div className='mobile-nav-items-2'>

                </div>


                <div id="hamburger-cross" onClick={handleMenuClose}>&#215;</div>
            </div>
        </div>
    );
}

export default Navbar;
