import React, { useState, useEffect } from 'react';
import '../Animation/Circles.css';

const NUM_SEGMENTS = 20;

const Circles = () => {
  const [positions, setPositions] = useState(
    Array.from({ length: NUM_SEGMENTS }, () => ({ top: "50%", left: "50%" }))
  );

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setPositions((prevPositions) => {
      const newPositions = [...prevPositions];
      newPositions[0] = { top: `${y}px`, left: `${x}px` };

      for (let i = 1; i < NUM_SEGMENTS; i++) {
        newPositions[i] = {
          top: prevPositions[i - 1].top,
          left: prevPositions[i - 1].left,
        };
      }

      return newPositions;
    });
  };

  return (
    <div className="card" onMouseMove={handleMouseMove}>
      {positions.map((pos, index) => (
        <div
          key={index}
          className="blob"
          style={{
            top: pos.top,
            left: pos.left,
          }}
        ></div>
      ))}
    </div>
  );
};

export default CIrcles;
