import React, { useRef, useEffect, useState } from 'react';
import { gsap } from 'gsap';

const SVGNS = 'http://www.w3.org/2000/svg';
const EASE = 0.7;

const Circles = () => {
  const [pointer, setPointer] = useState({
    x: window.innerWidth / 2,
    y: window.innerHeight / 2,
  });

  const svgRefs = useRef([]);

  useEffect(() => {
    const updatePointer = (event) => {
      setPointer({ x: event.clientX, y: event.clientY });
    };

    window.addEventListener('pointermove', updatePointer);

    return () => {
      window.removeEventListener('pointermove', updatePointer);
    };
  }, []);

  const createLine = (leader) => {
    const line = document.createElementNS(SVGNS, 'line');

    const get = gsap.getProperty(line);
    const set = gsap.quickSetter(line, 'attr');

    const genModifier = (prop) => {
      const pos2 = `${prop}2`;

      return () => {
        const linePos = get(prop);
        const leaderPos = leader(prop);

        const linePosNext = linePos + (leaderPos - linePos) * EASE;

        set({ [pos2]: leaderPos - linePosNext });

        return linePosNext;
      };
    };

    gsap.set(line, pointer);

    gsap.to(line, {
      x: '+=1',
      y: '+=1',
      repeat: -1,
      ease: 'linear',
      modifiers: {
        x: genModifier('x'),
        y: genModifier('y'),
      },
    });

    return line;
  };

  const createTrail = (color, length = 10) => {
    const svg = document.createElementNS(SVGNS, 'svg');
    let leader = (prop) => (prop === 'x' ? pointer.x : pointer.y);

    for (let i = 0; i < length; i++) {
      const line = createLine(leader);

      line.style.stroke = color;
      svg.appendChild(line);

      leader = gsap.getProperty(line);
    }

    document.body.appendChild(svg);
    svgRefs.current.push(svg);
  };

  useEffect(() => {
    createTrail('black', 30);
    createTrail('black', 20);
    createTrail('black', 10);

    return () => {
      svgRefs.current.forEach((svg) => {
        document.body.removeChild(svg);
      });
    };
  }, [pointer]);

  return null;
};

export default Circles;
